sysauto_script.script:
// Get today's time and date
var rightNow = new GlideDateTime();

// Query the database for NeedIt records with Due date field values within
// 5 days from now.  Only return NeedIt Task records that do not have
// a State field value of Closed Complete.
var dueSoon = new GlideRecord('x_58872_needit_needit');
dueSoon.addQuery('u_when_needed','>=',rightNow);
dueSoon.addQuery('u_when_needed','<',gs.hoursAgo(-120));
dueSoon.addQuery('state','!=',3);

dueSoon.query();
// Write a log message for each overdue NeedIt Task Record
while(dueSoon.next()){
	gs.info("NeedIt Request due soon record = " + dueSoon.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTask',dueSoon,dueSoon.number,gs.getUserName());
}
